            for(position in acehFoodNames.indices){
                val ieNaBuNa = IeNaBuNa()
                ieNaBuNa.name = acehFoodNames[position]
                ieNaBuNa.detail = acehFoodDetails[position]
                ieNaBuNa.photo = acehFoodsImages[position]
